import Cart from "./components/Cart";
import { Header } from "./components/Header";
import { useState, useEffect } from 'react';
import Main  from "./components/Main";

function App() {
  const [datas, setdatas] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5500`)
       .then((response) => response.json()
       .then(data => setdatas(data))
       .catch((error) => console.log(error))
    )
 }, [])

const [cartItems, setCartItems] = useState([]);


  const onAdd = (data) => {
    const exist = cartItems.find((x) => x._id === data._id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x._id === data._id ? { ...exist, qty: 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...data, qty: 1 }]);
    }
  };
  const onRemove = (data) => {
    const exist = cartItems.find((x) => x._id === data._id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x._id !== data._id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x._id === data._id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };



  return (
    <div class="bg-gray-100 ">    
      <Header/>
      <div class= " flex  justify-end space-x-5 container">
        <div class=" my-10 ">
          <Main class=" bg-white px-10 " datas={datas} onAdd={onAdd}></Main>
        </div>
        <Cart class=" px-10 ml-15 scroll-smooth hover:scroll-auto" cartItems={cartItems} onRemove={onRemove}></Cart>

      </div>
    </div>
  );
}

export default App;
