export default function Basket(props) {
  const { cartItems, onRemove } = props;

  return (
    <aside class="w-60 h-full bg-white px-10 col max-w-2xl mx-auto py-16 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">

      <h2>GÃ¼ndem Listesi</h2>
      <div class='relative'>
        {cartItems.map((item) => (
          <div key={item.id} >
            <div >{item.name}</div>
            <div >
            <button onClick={() => onRemove(item)} type="button" class="focus:outline-none text-white bg-red-700 hover:bg-red-800 focus:ring-4 focus:ring-red-300 font-medium rounded-lg text-sm px-5 py-1 mr-2 mb-2 dark:bg-red-600 dark:hover:bg-red-700 dark:focus:ring-red-900">
                Sil
              </button>{' '}

            </div>
          </div>
        ))}

          <>            
            <div >
              <button onClick={() => alert('Implement Checkout!')} class=" bg-white border-gray-200 dark:bg-gray-800 text-white text-sm leading-6 font-medium py-2 px-3 rounded-lg">
                Checkout
              </button>
            </div>

          </>
      </div>
    </aside>

  );
}
