import React from 'react';

export default function Basket(props) {
  const { cartItems, onRemove } = props;


  const handleSubmit = ()=>{
    fetch("http://localhost:5500/data", {
      method: "POST",
      headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      },
      body: JSON.stringify({gundem :cartItems}),
    })
  }

  const copyToClipboard = () => {
    let str = ""
    cartItems.map((card)=>{
        str = str + card.name + "\n";
    })
    str += "\ngundemimne.com";
    console.log(str)

    if (navigator && navigator.clipboard && navigator.clipboard.writeText)
      return window.navigator.clipboard.writeText(str);
    return Promise.reject('The Clipboard API is not available.');
  };
 function Example() {
    return (
      <div class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
  <strong class="font-bold">Holy smokes!</strong>
  <span class="block sm:inline">      Gündemlerin kopyalandı! Paylaşmaya Hazır
</span>
  <span class="absolute top-0 bottom-0 right-0 px-4 py-3">
    <svg class="fill-current h-6 w-6 text-red-500" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><title>Close</title><path d="M14.348 14.849a1.2 1.2 0 0 1-1.697 0L10 11.819l-2.651 3.029a1.2 1.2 0 1 1-1.697-1.697l2.758-3.15-2.759-3.152a1.2 1.2 0 1 1 1.697-1.697L10 8.183l2.651-3.031a1.2 1.2 0 1 1 1.697 1.697l-2.758 3.152 2.758 3.15a1.2 1.2 0 0 1 0 1.698z"/></svg>
  </span>
</div>

    );
  }


  const func=()=>{
    handleSubmit();
    copyToClipboard();
    Example();

  }
  return (
    <aside class=" lg:w-2/5  flex flex-col  gap-10">

      <h2>Gündem Listesi</h2>
      <div class='scroll-smooth hover:scroll-auto  flex flex-col min-w-0 break-words bg-white w-full mb-2 shadow-lg rounded-lg '>
        {cartItems.map((item) => (
          <div key={item._id} class="flex  justify-between my-2">
            <div class="">{item.name}</div>
            <div >
            <button onClick={() => onRemove(item)} type="button" class="px-10 ">
                Sil
              </button>{' '}

            </div>
          </div>
        ))}

          <>            
            <div >
              <button onClick={() =>func()} class="">
                Kopyala
              </button>
            </div>

          </>
      </div>
    </aside>

  );
}
