      method: "POST",
      headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      },
      body: JSON.stringify(cartItems),
    })
  }
  return (
    <aside class="">

      <h2>GÃ¼ndem Listesi</h2>
      <div class='relative'>
        {cartItems.map((item) => (
          <div key={item._id} >
            <div class="flex">{item.name}</div>
            <div >
            <button onClick={() => onRemove(item)} type="button" class="">
                Sil
              </button>{' '}

            </div>
          </div>
        ))}

          <>            
            <div >
              <button onClick={() =>handleSubmit()} class=" ">
                Checkout
              </button>
            </div>

          </>
      </div>
    </aside>

  );
}
