import Cart from "./components/Cart";
import { Header } from "./components/Header";
import { useState, useEffect } from 'react';
import Main  from "./components/Main";

function App() {
  const [datas, setdatas] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5500`)
       .then((response) => response.json()
       .then(data => setdatas(data))
       .catch((error) => console.log(error))
    )
 }, [])

const [cartItems, setCartItems] = useState([]);


  const onAdd = (data) => {
    const exist = cartItems.find((x) => x._id === data._id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x._id === data._id ? { ...exist, qty: 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...data, qty: 1 }]);
    }
  };
  const onRemove = (data) => {
    const exist = cartItems.find((x) => x._id === data._id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x._id !== data._id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x._id === data._id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };



  return (
    <div>    
      <Header/>
      <div class= "flex  max-w-6xl mx-auto space-y-2 justify-between space-x-5 container mt-10">
        <div class="flex shadow-md my-10 ">
          <Main class="w-3/4 bg-white px-10 " datas={datas} onAdd={onAdd}></Main>
          <Cart id="summary" class="w-1/4 px-10" cartItems={cartItems} onRemove={onRemove}></Cart>
        </div>
      </div>
    </div>
  );
}

export default App;
