import Cart from "./components/Cart";
import { Header } from "./components/Header";
import { useState, useEffect } from 'react';
import Main  from "./components/Main";

import data from "./data.js"





function App() {
  const [datas, setdatas] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5500`)
       .then((response) => response.json()
       .then(data => setdatas(data))
       .catch((error) => console.log(error))
    )
 }, [])
  //const { datas } = data;

const [cartItems, setCartItems] = useState([]);


  const onAdd = (data) => {
    const exist = cartItems.find((x) => x.id === data.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === data.id ? { ...exist, qty: 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...data, qty: 1 }]);
    }
  };
  const onRemove = (data) => {
    const exist = cartItems.find((x) => x.id === data.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== data.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === data.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };

  return (
    <div>    


    <Header/>
    <div class= "">
    <Main class="" datas={datas} onAdd={onAdd}></Main>
    <Cart class="" cartItems={cartItems} onRemove={onRemove}></Cart>
    </div>

    </div>
  );
}

export default App;
