export default function Basket(props) {
  const { cartItems, onRemove } = props;


  const handleSubmit = ()=>{
    fetch("http://localhost:5500/data", {
      method: "POST",
      headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
      },
      body: JSON.stringify({gundem :cartItems}),
    })
  }

  const copyToClipboard = () => {
    let str = ""
    cartItems.map((card)=>{
        str = str + card.name + "\n";
    })
    str += "\ngundemimne.com";
    console.log(str)

    if (navigator && navigator.clipboard && navigator.clipboard.writeText)
      return window.navigator.clipboard.writeText(str);
    return Promise.reject('The Clipboard API is not available.');
  };
 function Example() {
    return (

      alert("Gündemlerin kopyalandı! Paylaşmaya Hazır")

    );
  }


  const func=()=>{
    handleSubmit();
    copyToClipboard();
    Example();
  }

  return (
    <aside class=" lg:w-2/5  flex flex-col  gap-10 py-10">

      <div class='scroll-smooth hover:scroll-auto  flex flex-col min-w-0 break-words bg-white w-full mb-2 shadow-lg rounded-lg '>
        {cartItems.map((item) => (
          <div key={item._id} class="flex  justify-between my-2">
            <div class="">{item.name}</div>
            <div >




            </div>
          </div>
        ))}

          <>            
            <div >
              <button onClick={() =>func()} class="">
                Kopyala
              </button>
            </div>

          </>
      </div>
    </aside>

  );
}
