import React from 'react'

export const returnButton = (probs) => {

    const {cartItems,} = probs

    const handleSubmit = ()=>{
        fetch("http://localhost:5500/data", {
          method: "POST",
          headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
          },
          body: JSON.stringify({gundem :cartItems}),
        })
      }

      const copyToClipboard = () => {
        let str = ""
        cartItems.map((card)=>{
            str = str + card.name + "\n";
        })
        str += "\ngundemimne.com";
        console.log(str)

        if (navigator && navigator.clipboard && navigator.clipboard.writeText)
          return window.navigator.clipboard.writeText(str);
        return Promise.reject('The Clipboard API is not available.');
      };
     function Example() {
        return (

          alert("Gündemlerin kopyalandı! Paylaşmaya Hazır")

        );
      }


      const func=()=>{
        handleSubmit();
        copyToClipboard();
        Example();
      }


  return (
    <div >
    <button onClick={() =>func()} class="sticky">
      Kopyala
    </button>
  </div>
  )
}
