import Cart from "./components/Cart";
import { Header } from "./components/Header";
import { useState } from 'react';
import Main  from "./components/Main";

import data from "./data.js"
import { render } from "@testing-library/react";


class App extends React.Component {


   state = [cartItems, setCartItems] 


  onAdd = (data) => {
    const exist = cartItems.find((x) => x.id === data.id);
    if (exist) {
      setCartItems(
        cartItems.map((x) =>
          x.id === data.id ? { ...exist, qty: 1 } : x
        )
      );
    } else {
      setCartItems([...cartItems, { ...data, qty: 1 }]);
    }
  };
  onRemove = (data) => {
    const exist = cartItems.find((x) => x.id === data.id);
    if (exist.qty === 1) {
      setCartItems(cartItems.filter((x) => x.id !== data.id));
    } else {
      setCartItems(
        cartItems.map((x) =>
          x.id === data.id ? { ...exist, qty: exist.qty - 1 } : x
        )
      );
    }
  };
render(){
  return (
    <div>    


    <Header/>
